assignments.value:
=website: {{ $('Website Form Submission').item.json['Website URL'] }}

---

{{ $json.allContents.map(item => {
 let result = '';
  result += 'Page Title: ' + item.metadata.title + '\n';
  result += 'Page Description: ' + item.metadata.description + '\n';
  result += 'Page URL: ' + item.metadata.url + '\n\n';

  result += item.markdown;

return result;
}).join('\n------\n') }}
